import React, { useState } from 'react';

const PromptComponent = ({onSubmit }) =>{
    const [nimInput, setNimInput] = useState('');   
    const [namaInput, setNamaInput] = useState('');

    const handleNimChange = (event) => { 
        setNimInput(event.target.value);
    };

    const handleNamaChange = (event) => {
        setNamaInput(event.target.value);
    };

const handleSubmit = (event) => {
    event.preventDefault();
    onSubmit({ nim: nimInput, nama: namaInput});
    setNimInput('');
    setNamaInput('');
};

return (
    <div class="prompt">
        <form onSubmit={handleSubmit}>
        <label>
        Masukkan NIM:
        </label>
            <input type="text" value={nimInput} onChange={handleNimChange} />
        <label>
        Masukkan Nama:
        </label>
            <input type="text" value={namaInput} onChange={handleNamaChange} />
        <button type="submit">Submit</button>
    </form>
</div>
);
};
export default PromptComponent;
