import React, { useState } from 'react';
import PromptComponent from './PrompComponent';

const StateComponent = () => {
        const [submittedValues, setSubmittedValues] = useState([]);

    const handleSubmit = (inputValues) => {
        setSubmittedValues([...submittedValues, inputValues]);
    };

    return (
        <div>
            <table border="1">
                <thead>
                    <tr>
                        <th>NIM</th>
                        <th width="65%">Nama Mahasiswa</th>
                    </tr>
                </thead>
                <tbody>
                    {submittedValues.map((value, index) => (
                        <tr key={index}>
                            <td>{value.nim}</td>
                            <td>{value.nama}</td>
                        </tr>
                    ))}
                    </tbody>
                </table>
                <PromptComponent onSubmit={handleSubmit} />
            </div>
        );
};
export default StateComponent;
